import React, { Component } from 'react';
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux';
import { compose } from 'redux';
import { firestoreConnect } from 'react-redux-firebase';
import { getFirestore } from 'redux-firestore';
import { Modal, Button } from 'react-materialize';
import ZoomSaveCancel from './ZoomSaveCancel';
import ComponentElements from './ComponentElements';
import PropertiesToolbar from './PropertiesToolbar';
import JSONcontainer from './JSONcontainer';

class EditScreen extends Component {
    state = {
        name: '',
        owner: '',
    }

    sortCriteria = 'none';
    changedTime = false;

    updateTime = () => {
        console.log("updating time")
        let fireStore = getFirestore();
        fireStore.collection('wireframes').doc(this.props.wireframe.id).update({ time: Date.now() })
    }

    render() {
        const auth = this.props.auth;
        let wireframe = this.props.wireframe;

        if (!auth.uid) {
            return <Redirect to="/" />;
        }

        if (!wireframe)
            return <React.Fragment />

        if (!this.changedTime) {
            this.changedTime = true;
            this.updateTime();
        }

        return (
            <div className="edit-div">
                <div class="input-field">
                    <input id="name" type="text" class="validate"></input>
                    <label for="name">Name</label>
                </div>
                <div id="edit-modules" class="row">
                <div class="col s3">
                <div id="ZoomSaveCancel" class="row">
            <a className="clicker black-text col s2"><i class="material-icons">zoom_in</i></a>
            <a className="clicker black-text col s2"><i class="material-icons">zoom_out</i></a>
            <a className="clicker black-text col s4">Save</a>
            <a className="clicker black-text col s4">Cancel</a>
            </div>
                    <div className="col s12" style={{height: 70}}> </div>
                    <div id="ComponentElements" className="row">
            <div className="col s3" style={{height: 50}}> </div>
            <div className="white btn-large col s6" style={{borderStyle: "solid", borderColor: "black"}}></div>
            <div className="col s3" style={{height: 50}}> </div>
            <div className="col s12" style={{textAlign: "center"}}>Container</div>
            <div className="col s12" style={{height: 100}}> </div>
            <div className="btn-large-flat col s12" style={{textAlign: "center"}}>Prompt for Input:</div>
            <div className="col s12" style={{height: 10}}> </div>
            <div className="col s12" style={{textAlign: "center"}}>Label</div>
            <div className="col s12" style={{height: 100}}> </div>
            <div className="col s2" style={{height: 50}}> </div>
            <div className="grey btn col s8" style={{borderStyle: "solid", borderColor: "grey",
                textAlign: "left", textTransform: "none", color: "grey"}}>Input</div>
            <div className="col s2" style={{height: 50}}> </div>
            <div className="col s12" style={{textAlign: "center"}}>Textfield</div>
            </div>
                </div>
                <div className="col s6">
                <div className="grey lighten-2 container" style={{height: 500, width: 500, position: "absolute", overflow: "auto"}}>
                <Rnd id="JSONcontainer" className="white container" bounds="parent" default={{width: container.width/10, height: container.height/10, x: 0, y: 0}}>
                {controls && controls.map((controls) => {
                    if(controls.type === "label"){
                        console.log(controls.text);
                        return (<Rnd id="label" bounds="parent" className="btn-large-flat" bounds="parent" default={{width: controls.width/10, height: controls.height/10, x: controls.left/10, y: controls.top/10}} style={{textAlign: "center", 
                            background: controls.background, 
                            borderColor: controls.bordercolor,
                            borderWidth: controls.borderwidth,
                            fontSize: controls.fontsize,
                            color: controls.fontcolor
                            }}>{controls.text}</Rnd>)
                    }
                })}
            </Rnd>
                </div>
                </div>
                <div class="col s3">
                    <PropertiesToolbar></PropertiesToolbar>
                </div>
                <div className="col s12" style={{height: 30}}> </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    const { id } = ownProps.match.params;
    const { wireframes } = state.firestore.data;
    const wireframe = wireframes ? wireframes[id] : null;
    if (wireframe)
        wireframe.id = id;

    return {
        wireframe,
        auth: state.firebase.auth,
    };
};

export default compose(
    connect(mapStateToProps),
    firestoreConnect([
        { collection: 'wireframes' },
    ]),
)(EditScreen);
