          <h5>You are about to quit without saving your changes. Are you sure you want to do this?</h5>
          </Modal> : <>{children}</>;

        if (!this.changedTime) {
            this.changedTime = true;
            this.updateTime();
        }

        return (
            <div className="edit-div">
                <div class="input-field">
